{-# LANGUAGE OverloadedStrings #-}
import ProxyPool.Stratum

import Control.Monad.IO.Class

import Data.Aeson
import qualified Data.Vector as V
import qualified Data.ByteString.Lazy as B

import Test.Hspec

main :: IO ()
main = hspec $ do
    let subscription = "{\"id\": 2, \"method\": \"mining.subscribe\", \"params\": []}"
        auth         = "{\"id\": 3, \"method\": \"mining.authorize\", \"params\": [\"DReCBKnatV3DuWez4YJWkQfpUxGknzmkN\", \"asdf\"]}"
        submission   = "{\"params\": [\"DReCBKnatV3DuWez4YJWkQfpUxGknzmkN\", \"60192414783812809687929442797354436140\", \"01000000\", \"52c3976e\", \"4d020300\"], \"id\": 4, \"method\": \"mining.submit\"}"
        work         = "{\"params\": [\"60192414783812809687929442797354436140\", \"c85d074098410f97de49016207567b9beef1d71400b231e4bcdfe996c0a8e6e5\", \"\", \"00000000\", [\"2ec8a2a47bf9e7f02654c251a80a6c7d423baa71fd92d596dbd6ea00399bf5dc\", \"839f8e51bcb557fd60a20513e9d6039697194017a2ca94bee1508ea1e6de03f9\", \"36dd0609515caa2e4ac81e685132306633a9d0f046a7f378db625bccd80888cd\", \"6a33c30669d29daf2fe0236c6c95c53376f732ee62566ae4d34cdbd188bf08bf\"], \"00000001\", \"1c00c456\", \"52c3976e\", true], \"jsonrpc\": \"2.0\", \"method\": \"mining.notify\", \"id\": 694674178}"
        set_diff     = "{\"params\": [58.60378105659038], \"jsonrpc\": \"2.0\", \"method\": \"mining.set_difficulty\", \"id\": 1010601931}"
        initalise    = "{\"error\": null, \"jsonrpc\": \"2.0\", \"id\": 2, \"result\": [[\"mining.notify\", \"ae6812eb4cd7735a302a8a9dd95cf71f\"], \"\", 4]}"
        success      = "{\"error\": null, \"jsonrpc\": \"2.0\", \"id\": 4, \"result\": true}"
        failure      = "{\"error\": \"something\", \"jsonrpc\": \"2.0\", \"id\": 10, \"result\": null}"

    parseSpec (subscription, auth, submission, work, set_diff, initalise, success, failure)
    parseSpec generateJSON

parseSpec :: (B.ByteString, B.ByteString, B.ByteString, B.ByteString, B.ByteString, B.ByteString, B.ByteString, B.ByteString) -> Spec
parseSpec (subscription, auth, submission, work, set_diff, initalise, success, failure) = do
    -- helper function to check IDs
    let checkID rid value =
            case fromJSON rid :: Result Int of
                Error xs  -> expectationFailure "ID check failed"
                Success x -> x `shouldBe` value

    describe "Parse Stratum requests" $ do
        it "Parses subscription initalisation" $
            case decode subscription of
                Just (Request rid Subscribe) -> checkID rid 2
                _ -> expectationFailure "failed"

        it "Parse authorisation" $
            case decode auth of
                Just (Request rid (Authorize user pass)) -> do
                    checkID rid 3
                    user `shouldBe` "DReCBKnatV3DuWez4YJWkQfpUxGknzmkN"
                    pass `shouldBe` "asdf"
                _ -> expectationFailure "failed"

        it "Parse submission" $
            case decode submission of
                Just (Request rid (Submit worker job en2 ntime nonce)) -> do
                    checkID rid 4
                    worker `shouldBe` "DReCBKnatV3DuWez4YJWkQfpUxGknzmkN"
                    job    `shouldBe` "60192414783812809687929442797354436140"
                    en2    `shouldBe` "01000000"
                    ntime  `shouldBe` "52c3976e"
                    nonce  `shouldBe` "4d020300"
                _ -> expectationFailure "failed"

    describe "Parse Stratum notifications" $ do
        -- notifications do not require an ID
        it "Parse work notification" $
            case decode work of
                Just (Response rid (WorkNotify job prevHash cb1 cb2 merkle blockVer nBit nTime clean)) -> do
                    job             `shouldBe` "60192414783812809687929442797354436140"
                    prevHash        `shouldBe` "c85d074098410f97de49016207567b9beef1d71400b231e4bcdfe996c0a8e6e5"
                    cb1             `shouldBe` ""
                    cb2             `shouldBe` "00000000"
                    V.toList merkle `shouldBe` ["2ec8a2a47bf9e7f02654c251a80a6c7d423baa71fd92d596dbd6ea00399bf5dc", "839f8e51bcb557fd60a20513e9d6039697194017a2ca94bee1508ea1e6de03f9", "36dd0609515caa2e4ac81e685132306633a9d0f046a7f378db625bccd80888cd", "6a33c30669d29daf2fe0236c6c95c53376f732ee62566ae4d34cdbd188bf08bf"]
                    blockVer        `shouldBe` "00000001"
                    nBit            `shouldBe` "1c00c456"
                    nTime           `shouldBe` "52c3976e"
                    clean           `shouldBe` True
                _ -> expectationFailure "failed"

        it "Parse set difficulty" $
            case decode set_diff of
                Just (Response rid (SetDifficulty diff)) ->
                    diff `shouldBe` 58.60378105659038
                _ -> expectationFailure "failed"

    describe "Parse Stratum responses" $ do
        it "Parse initalisation" $
            case decode initalise of
                Just (Response rid (Initalise ex1 ex2Size)) -> do
                    checkID rid 2
                    ex1     `shouldBe` ""
                    ex2Size `shouldBe` 4
                _ -> expectationFailure "failed"

        it "Parse general response" $ do
            case decode success of
                Just (Response rid (General (Right _))) ->
                    checkID rid 4
                _ -> expectationFailure "failed"

            case decode failure of
                Just (Response rid (General (Left _))) ->
                    checkID rid 10
                _ -> expectationFailure "failed"

generateJSON :: (B.ByteString, B.ByteString, B.ByteString, B.ByteString, B.ByteString, B.ByteString, B.ByteString, B.ByteString)
generateJSON = ( encode $ Request (Number 2) subscription
               , encode $ Request (Number 3) auth
               , encode $ Request (Number 4) submission
               , encode $ Response Null work
               , encode $ Response Null set_diff
               , encode $ Response (Number 2) initalise
               , encode $ Response (Number 4) success
               , encode $ Response (Number 10) failure
               )
    where
        subscription = Subscribe
        auth         = Authorize "DReCBKnatV3DuWez4YJWkQfpUxGknzmkN" "asdf"
        submission   = Submit { s_worker      = "DReCBKnatV3DuWez4YJWkQfpUxGknzmkN"
                              , s_job         = "60192414783812809687929442797354436140"
                              , s_extraNonce2 = "01000000"
                              , s_nTime       = "52c3976e"
                              , s_nonce       = "4d020300"
                              }
        work         = WorkNotify { wn_job          = "60192414783812809687929442797354436140"
                                  , wn_prevHash     = "c85d074098410f97de49016207567b9beef1d71400b231e4bcdfe996c0a8e6e5"
                                  , wn_coinbase1    = ""
                                  , wn_coinbase2    = "00000000"
                                  , wn_merkle       = V.fromList ["2ec8a2a47bf9e7f02654c251a80a6c7d423baa71fd92d596dbd6ea00399bf5dc", "839f8e51bcb557fd60a20513e9d6039697194017a2ca94bee1508ea1e6de03f9", "36dd0609515caa2e4ac81e685132306633a9d0f046a7f378db625bccd80888cd", "6a33c30669d29daf2fe0236c6c95c53376f732ee62566ae4d34cdbd188bf08bf"]
                                  , wn_blockVersion = "00000001"
                                  , wn_nBit         = "1c00c456"
                                  , wn_nTime        = "52c3976e"
                                  , wn_clean        = True
                                  }
        set_diff     = SetDifficulty 58.60378105659038
        initalise    = Initalise "" 4
        success      = General $ Right $ Bool True
        failure      = General $ Left $ String "error"
