{-# LANGUAGE OverloadedStrings #-}
module POW (tests) where

import ProxyPool.Mining
import ProxyPool.Stratum

import Data.Aeson

import qualified Data.ByteString as B
import qualified Data.ByteString.Lazy.Builder as B
import qualified Data.ByteString.Lazy as BL

import qualified Data.ByteString.Base16 as B16

import Data.Maybe

import Test.Tasty
import Test.Tasty.Hspec

tests :: TestTree
tests = testGroup "POW" [ testCase "packInt" packIntSpec
                        , testCase "packHeader" packHeaderSpec
                        , testCase "unpackBE" unpackBESpec
                        , testCase "scrypt" scryptSpec
                        , testCase "submit" submitSpec
                        , testCase "toFromHex" toFromHexSpec
                        ]
packIntSpec :: Spec
packIntSpec = describe "Pack integers correctly" $ do
    it "Little endian pack test" $ do
        (BL.toStrict . B.toLazyByteString $ packIntLE 0x12345678 4) `shouldBe` B.pack [0x78, 0x56, 0x34, 0x12]


    it "Unpack integers correctly" $ do
        unpackIntLE (B.pack [0x78, 0x56, 0x34, 0x12]) `shouldBe` 0x12345678
        unpackIntLE (B.pack [0x78, 0x56, 0x34, 0x12, 0x00, 0x00, 0x00, 0x00]) `shouldBe` 0x12345678

unpackBESpec :: Spec
unpackBESpec = do
    describe "Test network byte order" $ do
        it "Handle 4 bytes correctly" $ do
            unpackBE (B.pack [1,2,3,4]) `shouldBe` B.pack [4,3,2,1]
        it "Handle more bytes correctly" $ do
            unpackBE (B.pack [1,2,3,4,5,6,7,8]) `shouldBe` B.pack [4,3,2,1,8,7,6,5]

