submitSpec :: Spec
submitSpec = do
    let work    = fromJust $ (\(Response _ resp) -> fromWorkNotify resp) =<< decode "{\"params\": [\"16590818011147399708813440887101803369\", \"5a10c4a0f53945bb727244c7b192587c38033f7ad279192161f6ef7439f33623\", \"\", \"00000000\", [\"938410e72e042bd4ff97b7192cb09e7b8918b3bb339d69ec4387e753d52ba9c1\", \"fb987320de633a22bb95fea56f2040b6e1d424beb4e7fc3aea7f92bb283e5e28\", \"36032f4a3abc24c3fa0a4442cf36c6854d0d123458868266c0b0c142d7b79819\", \"3c0ce3fcba7b76c65c3ac27d57388553236ef0d40016f493435c11df296a6beb\", \"ceb1c4893346a846e8fdb16d61ba68b0aaff890de78215c977a1d4eb97b2880d\", \"596de51bca7bd5e66aeafd190486428ba52e3494fe1e22b7a419aa0e283b333e\", \"8dd0fd508b06c6695a622ea52f92510355e8782074a9c0845082cdd7f6ffc53f\", \"44e95a272b94aa090af1e1866450a2f27c336ed5eb553d353e7e8c83d7a383fc\"], \"00000001\", \"1b369083\", \"52e1e237\", true], \"jsonrpc\": \"2.0\", \"method\": \"mining.notify\", \"id\": 200617238}"
        submit1 = fromJust $ (\(Request _ res) -> return res) =<< decode "{\"params\": [\"DReCBSKnatV3DuWez4YJWkQfpUxGknzmkN\", \"16590818011147399708813440887101803369\", \"01000000\", \"52e1e237\", \"920c0000\"], \"id\": 2, \"method\": \"mining.submit\"}"
        submit2 = fromJust $ (\(Request _ res) -> return res) =<< decode "{\"params\": [\"DReCBSKnatV3DuWez4YJWkQfpUxGknzmkN\", \"16590818011147399708813440887101803369\", \"01000000\", \"52e1e237\", \"10020200\"], \"id\": 3, \"method\": \"mining.submit\"}"

    describe "Correct POW on submissions" $ do
        it "Correct result 1" $ do
            getPOW submit1 work (0,0) (0,0) 4 scrypt `shouldBe` 177255867332298474018030488111423928422388421620745826718848526711306920
        it "Correct result 2" $ do
            getPOW submit2 work (0,0) (0,0) 4 scrypt `shouldBe` 72403129310559102547847627028645412322991108406547291525128594607097300

toFromHexSpec :: Spec
toFromHexSpec = do
    describe "toHex/fromHex isomorphism" $ do
        it "Correct result 1" $ do
            (toHex . fromHex $ "0001020304050607") `shouldBe` "0001020304050607"
